NGC.templates = {};
NGC.templates['entityPopupTemplate'] = Mustache.compile('<div id="entity-popup" class="{{css}} loader"></div>');
NGC.templates['alertsTemplate'] = Mustache.compile('<div id="alert_{{id}}" class="alert alert-{{type}}" style="display: none;"> <button type="button" class="close" data-dismiss="alert">&times;</button> <strong>{{statusText}}</strong> {{text}} </div>');
NGC.templates['versionsTemplate'] = Mustache.compile('<ul> <li> <b>v{{num}}</b> {{time}}, {{name}} </li> </ul>');
NGC.templates['entityPopupInfoTemplate'] = Mustache.compile('<table class="table table-striped"> {{#props}} <tr> <td>{{title}}</td> {{#text}} <td>{{text}}</td> {{/text}} {{^text}} <td>{{val}}</td> {{/text}} </tr> {{/props}} <tr> <td>Проверен</td> <td> {{#obj.approved}}Да{{/obj.approved}} {{^obj.approved}}Нет{{/obj.approved}} </td> </tr> {{#isBlocked}} <tr class="block"> {{#isUnBlocked}} <td>Заблокирована вами</td> <td> <button class="btn btn-small btn-primary block" id="unblock" type="button">Разблокировать</button> </td> {{/isUnBlocked}} {{^isUnBlocked}} <td>Заблокировал</td> <td>{{userBlocked}}</td> {{/isUnBlocked}} </tr> {{/isBlocked}} </table> <div class="edit"> {{#isUserEditor}} <button class="btn btn-small btn-primary {{#isBlock}}block{{/isBlock}}" id="edit" type="button" {{#editDenied}}disabled="disabled"{{/editDenied}}>Редактировать</button> {{/isUserEditor}} </div>');
NGC.templates['osmPopupTemplate'] = Mustache.compile('<div class="osm-popup"> <div class="caption"> <span>{{id}}</span> <a href="{{link}}" target="_blank" title="Посмотреть на OpenStreetMaps" class="osm"></a> </div> <table class="table table-striped"> {{#tags}} <tr> <td>{{key}}</td> <td>{{val}}</td> </tr> {{/tags}} </table> </div>');
NGC.templates['addressSearchTemplate'] = Mustache.compile('<ul class="{{cssClass}}"> {{#matches}} <li data-lat={{lat}} data-lon={{lon}} data-id={{id}}> {{display_name}} <a class="target" title="Перейти к объекту"></a> </li> {{/matches}} </ul>');
NGC.templates['welcomeTemplate'] = Mustache.compile('<div id="welcomePopup"> <p><strong>Здесь вы можете помочь проверить информацию по местоположениям объектов.</strong></p> <p>Чтобы начать работу - <a target="_blank" href="{{rootUrl}}register">зарегистрируйтесь</a> и найдите интересный вам участок или непроверенные объекты.</p> <p>Полезные ссылки:</p> <ul> <li><a target="_blank" href="http://nextgis.github.io/nextgiscrowd/">Руководство пользователя редактора</a></li> <li><a target="_blank" href="http://gis-lab.info/qa/uikgeo.html">О проекте</a></li> <li><a target="_blank" href="https://github.com/nextgis/nextgiscrowd">Исходный код</a></li> <li><a target="_blank" href="https://github.com/nextgis/nextgiscrowd/wiki/%D0%9A%D0%B0%D0%BA-%D1%8D%D1%82%D0%BE-%D1%80%D0%B0%D0%B1%D0%BE%D1%82%D0%B0%D0%B5%D1%82">Как это работает</a></li> <li>Обратная связь: <a a target="_blank" href="http://nextgis.ru/contact/">здесь</a></li> </ul> <p><strong>Пожалуйста, не используйте данные с других карт!</strong></p> {{#first}} <div class="start"> <input type="button" class="btn btn-primary" value="Начать работу!"/> </div> {{/first}} </div> ');
NGC.templates['searchResultsTemplate'] = Mustache.compile('<ul class="{{cssClass}}"> {{#entities}} <li data-lat={{lat}} data-lon={{lon}} data-id={{id}}> <span>{{name}}</span> {{addr}} <a class="target" title="Перейти"></a> {{#isAuth}}<a class="edit" title="Редактировать"></a>{{/isAuth}} </li> {{/entities}} </ul>');
NGC.templates['userLogsTemplate'] = Mustache.compile('<table class="table table-striped logs"> <caption>Общая статистика</caption> <tr> <th>Показатель</th> <th>Значение</th> </tr> <tr> <td>Всего объектов</td> <td class="stop">{{count_all}}</td> </tr> <tr> <td>Отредактировано объектов</td> <td class="stop">{{count_editable}}</td> </tr> <tr> <td>Отредактировано, %</td> <td class="stop">{{percent}}</td> </tr> </table> <table class="table table-striped logs"> <caption>Статистика по пользователям</caption> <tr> <th>Пользователь</th> <th>Кол-во объектов</th> </tr> {{#user_logs}} <tr> <td>{{user_name}}</td> <td class="stop">{{count_entities}}</td> </tr> {{/user_logs}} </table>');